function share(type, annoId, annoSubject, tmplatPath){
	var url = $(location).attr('protocol') + "//" + $(location).attr('host') + "/rcrt/view.do?annoId=" + annoId + "&lang=ko";
	var title = annoSubject;
	var shareUrl = "";

	if(type=="blog"){
		shareUrl = "http://blog.naver.com/openapi/share?url="+encodeURIComponent(url)+"&title="+encodeURIComponent(title);
	}else if(type=="cafe"){
		shareUrl = "https://share.naver.com/web/shareView.nhn?url="+encodeURIComponent(url)+"&title="+encodeURIComponent(title);
	}else if(type=="line"){
		shareUrl = "https://social-plugins.line.me/lineit/share"+"?url="+encodeURIComponent(url)+"&text="+encodeURI(title);
	}else if(type=="linked"){
		shareUrl = "http://www.linkedin.com/shareArticle?mini=true&url="+url+"&title="+encodeURIComponent(title)+"&source="+$(location).attr('host');
	}else if(type=="face"){
		shareUrl ="https://www.facebook.com/sharer/sharer.php?u="+encodeURIComponent(url)+"&t="+encodeURIComponent(title);
	}else if(type=="kakao"){
		var img = 'https://recruit.navercorp.com/share/tmplat/naver/img/recruit/share_'+tmplatPath+'.png';
		kakaoShare(title, url, img);
		return;
	}
	var open = window.open(shareUrl ,'sharePop','width=600,height=900,scrollbars=yes');
	open.focus();
}

