	})
}

function loadJobList() {
	if (!loadMoreFlag) return; // 더불러오기 플래그가 false면 종료
	if(isLoading) return; //이미 불러오는중이면 종료
	isLoading = true;	

	page++;
	startNum = pageSize * (page - 1);
	startNum = isNaN(startNum) ? 0 : startNum;
	endNum = Math.min(pageSize * page, totalRows);

	if (startNum <= totalRows) {
		//maskShow();
		loadingtext.show();

		var queryString = $("form[name=move_form]").serialize();
		$.ajax({
	        url:"/rcrt/loadJobList.do",
	        data:queryString + "&firstIndex=" + startNum,
	        dataType:"json",
	        async:false,
	        type:"get",
	        success:function(res){
	        	if(res.list.length > 0){
	        		drawJobList(res.list, endNum);
        		}
				else {
					$(".card_list").append('<p class="result_text">진행 중인 공고가 없습니다.</p>');
				}

				if (endNum == totalRows) {
					loadMoreFlag = false;
					loadingtext.hide();
				}
	        },
	        complete:function(e){
	        	//maskHide();
				loadingtext.hide();
				isLoading = false;
	        }
	    });
	}
}

