function copyUrl(annoId) {
	var url = $(location).attr('protocol') + "//" + $(location).attr('host') + "/rcrt/view.do?annoId=" + annoId + "&lang=ko";
	var dumTA = document.createElement("input"); 
	document.body.appendChild(dumTA); 
	dumTA.value = url; 
	dumTA.select();
	document.execCommand("copy");
	dumTA.remove();
}

