$(function() {
	var lnb = $(".lnb")
	var lnbFilter = $(".lnb .filter")
	var subContainer = $('.sub_container')
	var lnbFilterTop = lnbFilter.offset().top;


	subContainer.css('min-height', '100vh')

	function stickyNav(){
		var w = $(window);
		var scrolled = w.scrollTop();		

		if(scrolled > 0 && scrolled > lnbFilterTop) {			
			lnb.addClass("fixed");
			subContainer.addClass("fixed");
		} else {
			lnb.removeClass("fixed");
			subContainer.removeClass("fixed");
		}
	}


	$(window).scroll(function() {
		var w = $(window).outerWidth();
		if(w < 769){
			lnb.removeClass("fixed");
			subContainer.removeClass("fixed");
			return;
		}
		stickyNav();

	})

	$(window).on('resize', function(){
		var w = $(window).outerWidth();
		if(w < 769){
			lnb.removeClass("fixed");
			subContainer.removeClass("fixed");
		}else{
			stickyNav();
		}
	})
});

