function loadJobListRefresh() {
	loadMoreFlag = true;

	// 필터옵션 세팅
	setFilter();

	var pgmUrl = "list";
	if(pgmUrl == "view"){

		var form = document.search_mform;
		form.action = '/rcrt/list.do';
		form.submit();

	}else{

		page = 1;
		startNum = pageSize * (page - 1);
		startNum = isNaN(startNum) ? 0 : startNum;

		$(".card_list").empty();
		var queryString = $("form[name=search_mform]").serialize();

		$.ajax({
		    url:"/rcrt/loadJobList.do",
		    data:queryString + "&firstIndex=" + startNum,
		    dataType:"json",
		    async:false,
		    type:"get",
		    success:function(res){
		   		endNum = Math.min(pageSize * page, res.totalSize);
		   		totalRows = res.totalSize;

		    	if(res.list.length > 0){
		    		drawJobList(res.list, endNum);
		   		}
				else {

					$(".card_list").append('<p class="result_text">진행 중인 공고가 없습니다.</p>');

				}

		   		if (endNum == totalRows) {
					loadMoreFlag = false;
				}
		    },
		    complete:function(e){
		    }
		});

		// url 수정
		var urlString = $("form[name=search_mform]").serialize() ;
		changeUrl("/rcrt/list.do?"+urlString);
	}
}

function setFilter() {
	var form = document.search_mform;
	var mform = document.move_form;

	// 검색어
	mform.sw.value = form.sw.value;

	// 직군.직무
	var subJobCd = "";

	for (var i = 0; i < form.subJobCdData.length; i++) {
		if(!form.subJobCdData[i].disabled) {
			if (subJobCd != "") {
				subJobCd += ",";
			}
			subJobCd += form.subJobCdData[i].value;
		}
	}
	form.subJobCdArr.value = subJobCd;
	mform.subJobCdArr.value = subJobCd;
	//$("input[name=subJobCdData]").remove();

	// 법인 
	var sysCompanyCd = "";
	if(form.sysCompanyCdData != null){
		for (var i = 0; i < form.sysCompanyCdData.length; i++) {
			if(!form.sysCompanyCdData[i].disabled) {
				if (sysCompanyCd != "") {
					sysCompanyCd += ",";
				}
				sysCompanyCd += form.sysCompanyCdData[i].value;
			}
		}
		form.sysCompanyCdArr.value = sysCompanyCd;
		mform.sysCompanyCdArr.value = sysCompanyCd;
		//$("input[name=sysCompanyCdData]").remove();
	}

	// 고용형태
	var empTypeCd = "";
	for (var i = 0; i < form.empTypeCdData.length; i++) {
		if(!form.empTypeCdData[i].disabled) {
			if (empTypeCd != "") {
				empTypeCd += ",";
			}
			empTypeCd += form.empTypeCdData[i].value;
		}
	}
	form.empTypeCdArr.value = empTypeCd;
	mform.empTypeCdArr.value = empTypeCd;
	//$("input[name=empTypeCdData]").remove();

	// 경력
	var entTypeCd = "";
	for (var i = 0; i < form.entTypeCdData.length; i++) {
		if(!form.entTypeCdData[i].disabled) {
			if (entTypeCd != "") {
				entTypeCd += ",";
			}
			entTypeCd += form.entTypeCdData[i].value;
		}
	}
	form.entTypeCdArr.value = entTypeCd;
	mform.entTypeCdArr.value = entTypeCd;
	//$("input[name=entTypeCdData]").remove();

	// 근무지
	var workAreaCd = "";
	if (typeof form.workAreaCdData !== 'undefined') {
		for (var i = 0; i < form.workAreaCdData.length; i++) {
			if(!form.workAreaCdData[i].disabled) {
				if (workAreaCd != "") {
					workAreaCd += ",";
				}
				workAreaCd += form.workAreaCdData[i].value;
			}
		}
	}
	form.workAreaCdArr.value = workAreaCd;
	mform.workAreaCdArr.value = workAreaCd;
	//$("input[name=workAreaCdData]").remove();
}

function changeUrl(url){
	if (typeof (history.pushState) != "undefined") {
		history.pushState(null, null, url);
	}else{
		location.href = url;
	}
}

